mapper.select:
SELECT p.brand_name p_brandName, p.name p_name, p.category_name p_categoryName, p.sn p_sn, p.sub_title p_subTitle, p.cover_picture p_coverPicture, p.description p_description, p.original_price p_originalPrice, p.sale_price p_salePrice, p.stock p_stock, p.weight p_weight, p.keywords p_keywords, p.detail_title p_detailTitle, p.detail_desc p_detailDesc, p.created_at p_createdAt,
sku.sku_code sku_skuCode, sku.picture sku_picture, sku.price sku_price, sku.promotion_price sku_promotionPrice, sku.stock sku_stock
FROM product p LEFT JOIN product_sku sku ON sku.product_id = p.id WHERE p.id = #{productId}
